// Simulating the uniforms as constants - adjust these as needed
#define POLYCHROME vec2(0.5, 0.5)
#define TEXTURE_DETAILS vec4(0.0, 0.0, 1.0, 1.0)  // xy: offset, zw: scale
#define IMAGE_DETAILS vec2(1.0, 1.0)
#define HOVER_STRENGTH .2;

float hue(float s, float t, float h) {
    float hs = mod(h, 1.0) * 6.0;
    if (hs < 1.0) return (t-s) * hs + s;
    if (hs < 3.0) return t;
    if (hs < 4.0) return (t-s) * (4.0-hs) + s;
    return s;
}

vec4 RGB(vec4 c) {
    if (c.y < 0.0001)
        return vec4(vec3(c.z), c.a);

    float t = (c.z < 0.5) ? c.y*c.z + c.z : -c.y*c.z + (c.y+c.z);
    float s = 2.0 * c.z - t;
    return vec4(hue(s,t,c.x + 1.0/3.0), hue(s,t,c.x), hue(s,t,c.x - 1.0/3.0), c.w);
}

vec4 HSL(vec4 c) {
    float low = min(c.r, min(c.g, c.b));
    float high = max(c.r, max(c.g, c.b));
    float delta = high - low;
    float sum = high+low;

    vec4 hsl = vec4(0.0, 0.0, 0.5 * sum, c.a);
    if (delta == 0.0)
        return hsl;

    hsl.y = (hsl.z < 0.5) ? delta / sum : delta / (2.0 - sum);

    if (high == c.r)
        hsl.x = (c.g - c.b) / delta;
    else if (high == c.g)
        hsl.x = (c.b - c.r) / delta + 2.0;
    else
        hsl.x = (c.r - c.g) / delta + 4.0;

    hsl.x = mod(hsl.x / 6.0, 1.0);
    return hsl;
}

// Mouse hover effect calculation
vec2 calculateHoverEffect(vec2 position, vec2 mousePos) {

    vec2 screenCenter = iResolution.xy * 0.5;
    float midDist = length(position - screenCenter) / length(iResolution.xy);
    vec2 mouseOffset = (position - mousePos);
    float hoverStrength = HOVER_STRENGTH;

    float scale = 0.2 * (-0.03 - 0.3 * max(0.0, 0.3 - midDist))
                 * hoverStrength * (length(mouseOffset) * length(mouseOffset))
                 / (2.0 - midDist);

    return position + vec2(0.0, scale);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Apply hover effect to fragment coordinate
    vec2 adjustedCoord = calculateHoverEffect(fragCoord, iMouse.xy);

    // Convert fragment coordinates to UV coordinates
    vec2 uv = adjustedCoord/iResolution.xy;

    // Sample the input texture (iChannel0 in ShaderToy)
    vec4 tex = texture(iChannel0, uv);
    vec2 texture_coords = uv;

    // Calculate UV for effects
    vec2 effect_uv = (((texture_coords)*(IMAGE_DETAILS)) - TEXTURE_DETAILS.xy*TEXTURE_DETAILS.ba)/TEXTURE_DETAILS.ba;

    float low = min(tex.r, min(tex.g, tex.b));
    float high = max(tex.r, max(tex.g, tex.b));
    float delta = high - low;

    float saturation_fac = 1.0 - max(0.0, 0.05*(1.1-delta));

    vec4 hsl = HSL(vec4(tex.r*saturation_fac, tex.g*saturation_fac, tex.b, tex.a));

    float t = POLYCHROME.y*2.221 + iTime;
    vec2 floored_uv = (floor((effect_uv*TEXTURE_DETAILS.ba)))/TEXTURE_DETAILS.ba;
    vec2 uv_scaled_centered = (floored_uv - 0.5) * 50.0;

    vec2 field_part1 = uv_scaled_centered + 50.0*vec2(sin(-t / 143.6340), cos(-t / 99.4324));
    vec2 field_part2 = uv_scaled_centered + 50.0*vec2(cos( t / 53.1532),  cos( t / 61.4532));
    vec2 field_part3 = uv_scaled_centered + 50.0*vec2(sin(-t / 87.53218), sin(-t / 49.0000));

    float field = (1.0+ (
        cos(length(field_part1) / 19.483) + sin(length(field_part2) / 33.155) * cos(field_part2.y / 15.73) +
        cos(length(field_part3) / 27.193) * sin(field_part3.x / 21.92) ))/2.0;

    float res = (0.5 + 0.5* cos( (POLYCHROME.x) * 2.612 + ( field + -0.5 ) *3.14));
    hsl.x = hsl.x + res + POLYCHROME.y*0.04;
    hsl.y = min(0.6, hsl.y+0.5);

    tex.rgb = RGB(hsl).rgb;

    if (tex.a < 0.7)
        tex.a = tex.a/3.0;

    // Final color output
    fragColor = tex;
}
