const vec2 vp = vec2(320.0, 200.0);

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float t = iTime * 5.0;
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p0 = (uv - 0.5) * vp;
    vec2 hvp = vp * 0.5;

    vec2 p1d = vec2(cos(t / 50.0), sin(t / 80.0)) * hvp - p0;
    vec2 p2d = vec2(sin(-t / 70.0), cos(-t / 60.0)) * hvp - p0;
    vec2 p3d = vec2(cos(-t / 90.0), sin(t / 30.0)) * hvp - p0;
    float sum = 0.5 + 0.5 * (
        cos(length(p1d) / 30.0) +
        cos(length(p2d) / 20.0) +
        sin(length(p3d) / 25.0) * sin(p3d.x / 20.0) * sin(p3d.y / 15.0)
    );


    vec3 color = vec3(
        0.3 + 0.7 * cos(sum * 3.14 + t * 0.2),  
        0.4 + 0.6 * sin(sum * 6.28 + t * 0.15), 
        0.5 + 0.5 * cos(sum * 9.42 - t * 0.25)  
    );


    color *= 0.8 + 0.2 * sin(t + sum * 3.0);


    fragColor = vec4(color, 1.0);
}
