void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // Normalized Mouse coordinates (from 0 to 1)
    vec2 mouse = iMouse.xy / iResolution.xy; 
    vec4 tex = texture(iChannel1, uv);

    float exp = 3.0;


    float displacementX = pow(distance(uv.x, mouse.x), exp);
    float displacementY = pow(distance(uv.y, mouse.y), exp);
    vec2 displacement = 1.0 - vec2(displacementX, displacementY);

    vec2 displacedUV = uv + tex.rg * displacement * 0.1;
    vec4 source = texture(iChannel0, displacedUV);  

    fragColor = vec4(source);

//    fragColor = vec4(vec3(displacement.x),1.0);
}
