void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 uv2 = texture(iChannel0, uv).xyz;
    vec3 uv3 = texture(iChannel1, uv).xyz;
    float p = sin(iTime*4.)*.5 + .5;
    vec2 newv = (p*uv2 + (1.-p)*uv3).xy;
    float s = max(iMouse.x/iResolution.x, .01);
    vec3 col = texture(iChannel2, newv*s).xyz;
    fragColor = vec4(col,1.0);
}
