vec2 kale(vec2 uv, float angle, float base, float spin) {
	float a = atan(uv.y,uv.x)+spin;
	float d = length(uv);
	a = mod(a,angle*2.0);
	a = abs(a-angle);
	uv.x = sin(a+base)*d;
	uv.y = cos(a+base)*d;
    return uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy*2.-1.;
    float pi = 3.14159265359;
    float t = iTime/432.+1000.;
    uv = kale(uv, pi/6.0, pi * sin(t*5.)/3., pi/2.0);

    uv.y = sin(uv.x*4.)*10. + sin( uv.y + t ) * 10.;

    float b = length(uv)*2.;
    float c = 33.;

    uv += vec2(cos(b * c ),sin(b*c));



    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(5,3,4));

    // Output to screen
    fragColor = vec4(col,1.0);
}
