mat2 R2D(float rad){
    return mat2(cos(rad), sin(rad), -sin(rad), cos(rad));
}

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e,h; vec3 p;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*iResolution.xy)/iResolution.y,1)+e;
    p.z-=((iTime*.2));p.x-=.5;
    p.xy*=R2D(7.);

    for(j=0.;j++<8.;)
        p=abs(abs(mod(p,i*.2)))-.01,
        d=dot(length(p.xy),p.z);

    e+=d;

}o+=vec4(-log(p*p),1)*5./i;
}
