float sinStep(float pos,float phase, float freq, float seed)
{
    float f = freq*texture(iChannel0,vec2(seed/100.0)).x;
    return sin(pos * f - phase)/f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scale = 2.0;
    float speed = 5.0;
    vec2 uv = scale*(2.*fragCoord/iResolution.y);
    int iterations = 50;
    vec3 col = vec3(0.0);
    for(int i = 1; i < iterations;i++)
    {
        col += 0.5*vec3(sinStep(uv.x,speed*iTime,float(i),float(i)));
        col += 0.5*vec3(sinStep(uv.y,speed*iTime,float(i),float(i)));
    }
    fragColor = vec4((col+1.0)/5.0,1.0);
}
