float lengthSquared(vec2 v) {
    return v.x * v.x + v.y * v.y;
}

vec2 complexMultiplication(vec2 v1, vec2 v2) {
    // new real = reals multiplied together - imaginaries multiplied together (i^2 = -1)
    // new imaginary = imaginary multiplied by real + real multipleid by imaginary
    return vec2(v1.x * v2.x - v1.y * v2.y, v1.y * v2.x + v1.x * v2.y);
}

void mainImage( out vec4 col, in vec2 xy )
{
    vec2 res = iResolution.xy;
    int maxIters = min(200, int(iTime * 50.0f));
    const float left = -2.0f;
    const float right = 1.0f;
    const float top = 1.125f;
    const float bottom = -1.125f;

    vec2 c = vec2(left + (xy.x * (right - left) / res.x), top + (xy.y * (bottom - top) / res.y));
    vec2 z = vec2(0);

    int i = 0;
    while (i < maxIters && lengthSquared(z) < 4.0f) {
        z = complexMultiplication(z,z) + c;
        ++i;
    }

    col = vec4(1.0f * float(i >= maxIters), 0, 0, 1);
}
