void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = fract( 10.* (2.*u - R ) / R.y ) -.5;
    O = mix( vec4(37,0,183,0), vec4(0,66,9,0), smoothstep( 15./R.y ,0., length(U)-.2) ) / 183.;
 // O = sqrt( mix( vec4(0,0,1,0), vec4(0,.15,0,0), smoothstep( 15./R.y ,0., length(U)-.2) ) );
}
