// Inigo Quilez
vec3 palette(float t){
    vec3 a = vec3(0.5);
    vec3 b = vec3(0.5);
    vec3 c = vec3(1.0, 0.7, 0.4);
    vec3 d = vec3(0.0, 0.15, 0.2);

    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int maxIterations = 500;
    vec2 zoomCenter = vec2(0.001643721971153, -0.822467633298876);
    float zoomSpeed = 2.0;


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Centered at 0,0
    uv = uv - 0.5;
    float zoomNum = 4.0 * pow(0.8, zoomSpeed*iTime);
    uv *= zoomNum;

    uv.x *= iResolution.x/iResolution.y;
    uv += zoomCenter;


    float zr = 0.0;
    float zi = 0.0;
    int iterations = 0;


    for (int i = 0; i <maxIterations; i++){
        float xtemp = zr*zr - zi*zi + uv.x;
        zi = 2.0*zr*zi + uv.y;
        zr = xtemp;
        iterations += 1;

        if (zr*zr + zi*zi > 2.0){
            break;
        }
    }

    vec3 col = palette(float(iterations)/float(maxIterations));

    // Output to screen
    fragColor = vec4(col,1.0);
}
