void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float xCircles = 4.0;
    float yCircles = 3.0;


    // Normalize Coords
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= float(xCircles);
    uv.y *= float(yCircles);

    // Intersection check
    float n = 0.0;
    n += float(distance( mod(uv.xy, vec2(1.0)), vec2(0.5,0.5)) < 0.5);
    n += float(distance( mod(uv.xy + vec2(0.5), vec2(1.0)), vec2(0.5,0.5)) < 0.5);

    // Colors
    vec4 top = vec4(0.3 * n, 0.6 * n, 0.2 * n, 1.0);
    vec4 mid = vec4(0.8 * n, 0.5 * n, 0.1 * n, 1.0);
    vec4 bot = vec4(0.4 * n, 0.2* n, 0.1 * n, 1.0);

    // Fallback color
    fragColor = vec4(1.0, 0.0, 1.0, 1.0);

    // Vertical
    if(uv.y < 3.0) {
        fragColor = top;
    }

    if(uv.y < 2.0 ) {
        fragColor = mid;
    }

    if(uv.y < 1.0) {
        fragColor = bot;
    }

    // Horizontal
    if(uv.x > 0.0 && n == 1.0) {
       fragColor *= 1.0;
    }

    if(uv.x > 1.0 && n == 1.0) {
       fragColor *= 0.9;
    }

    if(uv.x > 2.0 && n == 1.0) {
       fragColor *= 0.8;
    }

    if(uv.x > 3.0 && n == 1.0) {
       fragColor *= 0.7;
    }


}
