#define PI atan(0., -1.)
#define TAU5 2. * PI / 5.

float sdPT(vec2 p, float radius){
    vec2 q;
    float radial, repeat, star, a, e, d;

    radial = atan(p.x , -p.y);
    repeat = mod(radial, TAU5) - TAU5 / 2.;
    repeat = abs(repeat) - TAU5;
    star   = cos(repeat) * length(p) - .13;

    q = abs(p + vec2(.015, .02)) - vec2(.03, .12);
    q.x = abs(q.x - .055) - .03;
    d = max(q.x, q.y);

    q = abs(p + vec2(-.07, -.07)) - vec2(.07, .03);
    e = max(q.x, q.y);

    q = abs(p - vec2(-.065, .035)) - vec2(.01, .01);
    a = abs(length(max(q, 0.)) - .03) - .025;

    return max(star, -min(a, min(d, e)));
}


void mainImage(out vec4 O, vec2 u){
    vec2 R = iResolution.xy, 
         p = .7 * (u - R/2. + cos(u.x * .043 + u.y * .03 + iTime * 10.) * 7. ) / R.y;

    float d = sdPT(p, .4),
          s = smoothstep(3. / R.y, 0., d);

    O = vec4(.75, 0, 0, 1) + s;
}
