uvec4 m(uvec4 a) { return a * a.yzwx ^ a; }                          // Noise Step.
vec4 h(vec2 v) { return vec4(m(m(uvec4(v, v + 3e7)))) / vec4(-1u); } // 2-Step Noise.

void mainImage(out vec4 c, vec2 v) { c = h(v); } // Only to display it here.
