// Sign..? Again? By Rik Riesmeijer, 2024 - CC0.
void mainImage(out vec4 c, vec2 v) {
    c.ba = c.rg = v / (v + v);
    v += v += v = v / iResolution.xy - c.r;
    float g = -float(int(v.x += iTime + iDate.x)) + v.x - c.r;
    float f = v.x + v.x - float(int(v.x + v.x));
    f += f += f = f * f - f;
    c += sign(f * sign(g) - v.y) * c.r;
}
