void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy * 0.17;


     vec3 col = texture(iChannel0, uv).rgb;


    // Output to screen
    fragColor = vec4(col, 1.0);
}

int getAliveNeighbors(ivec2 p) {
    int numAlive = 0;

    for (int y = -1; y <= 1; y++) {
        for (int x = -1; x <= 1; x++) {
            if (x == 0 && y == 0) continue;
            numAlive += texelFetch(iChannel1, p + ivec2(x, y), 0).r > 0.5 ? 1 : 0;
        }
    }
    return numAlive;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec4 col = vec4(0);

    if (iFrame < 1)
        col = texture(iChannel0, uv).rrrr;
    else if (iFrame % 20 != 1) {
        col = texelFetch(iChannel1, ivec2(fragCoord),0);
    }
    else {
        bool isAlive = texelFetch(iChannel1, ivec2(fragCoord),0).r > 0.5;
        int numAliveNeighbors = getAliveNeighbors(ivec2(fragCoord));

        int nextState = 0;

        if (isAlive && (numAliveNeighbors == 2 || numAliveNeighbors == 3))
            nextState = 1;
        else if (!isAlive && numAliveNeighbors == 3)
            nextState = 1;
        else
            nextState = 0;

        col = vec4(vec3(nextState), 1.0);
    }


    if (iMouse.z > 0.5 && distance(iMouse.xy, fragCoord) < 30.0)
        col = vec4(1);

    fragColor = col;
}
