float triangle (vec2 uv, float pct){
      return  step( pct-1.,  uv.y) -
              step( pct, uv.y);
}

    vec3 col1 = vec3(.08,0.76,0.91);
    vec3 col2 = vec3(1.,1.,0.1);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv =(fragCoord-.5*iResolution.xy)/iResolution.y;
    uv = fract(uv*10.);
    vec3 color = vec3(0.0);
	vec3 mask = vec3(0);
    mask = mix(color,vec3(1),triangle(uv,1.-uv.x));    
	color = mix(col1,col2,mask);

    fragColor = vec4(color,1.0);

}
