// Fork of "Electron MonteCarlo" by wyatt. https://shadertoy.com/view/XcGGW3
// 2024-04-17 13:19:00

// Fork of "Bragg Peak" by wyatt. https://shadertoy.com/view/4cKGRK
// 2024-04-16 22:48:42

Main {
    Q = vec4(0);


    Q = A(U)/float(iFrame);

    Q = sin(-2.+6.*Q.x+vec4(1,2,3,4));



    for (float i = 0.; i < N; i++) {
        vec4 q  = A(vec2(U.x,.5+i))/float(iFrame);

        vec2 u = U/R;
        q.y *= .15;
        if (!isnan(q.y))
        Q += exp(-.25*(u.y-q.y)*(u.y-q.y)*R.x*R.x);
    }


}

Main {

    if (U.y > N) discard;

    vec3 p = vec3(hash(vec4(U,iFrame,iTime)).xyz*12.-6.);
    vec3 v = vec3((1.5*(2.+U.y)/N),0,0);
    vec4 h;
    for (float i = 0.; i < U.x; i++) {

        h = hash(vec4(U,i,iFrame));
        h.xyz = length(v)*normalize(h.xyz*2.-1.)*(.2+1./(.1+2.*dot(v,v)));
        h.xyz = 1.*h.xyz*dot(normalize(h.xyz),normalize(v));
        v -= .007*h.xyz;

        p += v;
    }
    Q = vec4(p,length(h.xyz));
    if (isnan(Q.x)||isnan(Q.y)||isnan(Q.z)||isnan(Q.w)) Q = vec4(0);

}

#define N 10.

#define R (iResolution.xy)
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define C(U) textureiChannel2,(U)/R)
#define D(U) texture(iChannel3,(U)/R)
#define Main void mainImage(out vec4 Q, in vec2 U) 

float line (vec2 p, vec2 a, vec2 b) { 
    return length(p-a-(b-a)*clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.));
}

#define ei(a) mat2(cos(a),sin(a),-sin(a),cos(a))

vec4 hash(vec4 p4)
{
	p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

Main {
    Q = B(U);
    for (float i = 0.; i< R.x; i++) {

        vec4 q = A(vec2(.5+i,U.y));
        if (!isnan(q.w)&&!isnan(q.x))
        Q.y += q.w*exp(-pow(q.x-U.x,2.));


        {vec4 q = A(.5+vec2(i,N-4.));
        if (!isnan(q.w)&&!isnan(q.x)&&!isnan(q.y))
        Q.x += .5*q.w*exp(-.5*pow(length(U-q.xy-vec2(0,.5*R.y)),2.));}
    }

}
