Main {
    float p = 0.;

    Q = 1.-sin(-2.+atan(B(U).x)+vec4(1,2,3,4));

}

uint baseHash(uvec2 p){
    p = 1103515245U*((p >> 1U)^(p.yx));
    uint h32 = 1103515245U*((p.x)^(p.y>>3U));
    return h32^(h32 >> 16);
}
vec3 hash(vec2 x){
    uint n = baseHash(floatBitsToUint(x));
    uvec3 rz = uvec3(n, n*16807U, n*48271U);
    return vec3((rz >> 1) & uvec3(0x7fffffffU))/float(0x7fffffff);
}

Main {
    if (U.y>=2.) discard;

    vec4 v = A(vec2(U.x,1.5));
    vec4 p = A(vec2(U.x,.5));


    vec3 h = hash(p.xy);
    vec3 h2 = hash(p.yz);
    vec3 g = (h.xyz*2.-1.)*(h2.z>.001?.01:1.);

    v.xyz += -.001*v.xyz + g*length(v.xyz)  -  cross(v.xyz,vec3(-0.003,.00,.005));
    p.xyz += 3.*v.xyz*inversesqrt(1.+dot(v.xyz,v.xyz));

    if (iFrame < 1) {

        v = vec4(.05*U.x,vec2(.01,.1)*(hash(U).xy*2.-1.),1);
        p = vec4(.5*R.x,-.1*R.y,0,1);

    }
    if (U.y<1.) Q = p; else Q = v;
}

Main {
    float p = 0.;
    for (float i = 0.; i < N; i++) {

        p += 1./(1.+length(U-A(.5+vec2(i,0)).xy));
    }

    Q = max(B(U),p);

}
