#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define Main void mainImage(out vec4 Q, in vec2 U)

vec4 hash(vec4 p4)
{
	p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

Main {

    Q = A(U);
    vec4 h = hash(vec4(U,iTime,iFrame));
    float a = h.x*3.141592658*2.;
    vec2 u = U;
    vec2 v = h.y*vec2(cos(a),sin(a));

    for (float i = 0.; i < 300.; i++) {
        Q += 100000.*step(length(u-vec2(.8,.5)*R),20.)*
            exp(-100.*length(v-vec2(1.,0)));
        u += (.5*R.x-u.x<100.?10.:2.)*v;
        v += (.5*R.x-u.x<100.?0.:.1)*(hash(vec4(u,i,iFrame)).xy*2.-1.);
    }
}
