const int DIGITS[] = int[](0x6999996, 0x7222262, 0xf842196, 0x6916196, 0x1117531, 0x691688f, 0x699e896, 0x884421f, 0x6996996, 0x1117996);
const int MAX_DIGITS = 5;
const int MAX_PREC = 2;
const int SPACE = 5;

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.y * vec2(7.0, 4.0) - 0.25;
    vec3 color = vec3(0.0);

    float fTime = iTime;
    for (int i = 0; i < MAX_PREC; i++) fTime *= 10.0;
    int time = int(floor(fTime));

    ivec2 p = ivec2(floor(uv * vec2(4, 7)));
    int place = MAX_DIGITS - p.x / MAX_DIGITS;
    if (place >= 0 && place <= MAX_DIGITS) {
        for (int i = 0; i < place; i++) time /= 10;
        int digit = time % 10;
        int bits = DIGITS[digit];
        p.x %= SPACE;
        p = ivec2(3, 6) - p;
        if (p.x >= 0 && p.x < 4 && p.y >= 0 && p.y < 7)
            color += float((bits >> ((p.y << 2) + p.x)) & 1);
    }

    fragColor = vec4(color, 1.0);
}
