//Creates Box at any size and position on screen.
float box(vec2 size, vec2 st, vec2 pos)
{
    st -= pos;
    vec2 size2 = vec2(1.-size.x,1.-size.y);
    float fac1 = step(size.x,st.x);
    float fac2 = step(size2.x,st.x);
    float fac3 = step(size.y,st.y);
    float fac4 = step(size2.y,st.y);
    return (fac1 - fac2) * (fac3-fac4);
}

//Creates smooth edged circle.
float circle(float radius, vec2 st, vec2 pos)
{
    st -= pos;
    float circ = length(st)-radius;
    circ = smoothstep(0.001,0.0,circ);
    return circ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Center Normalized pixel coordinates
    vec2 uv = ( 2. * fragCoord - iResolution.xy ) / iResolution.y;

    //Set up Noise Texture
    float noise = texture(iChannel0,uv).x;

    //Animate it using sin wave scaled 0-1. Smoothstep as threshold.
    //Noise as factor. All Normalized.
    float aN = normalize(smoothstep((0.5+0.5*sin(iTime)),0.,noise));

    // Setup capsule shape using shape functions
    float box = box(vec2(-.3,.3),uv,vec2(-.5,-.5));
    float c = circle(.2,uv,vec2(.8,0.));
    float c2 = circle(.2,uv,vec2(-.8,0.));

    //Normalized cause where circles intersected box. It equaled 2 instead of 1 so
    //this fixes that.
    float capsule = normalize((box + c + c2));

    //Apply animated noise texture to capsule.
    capsule = capsule * aN;

    // Output to screen
    fragColor = vec4(vec3(capsule),1.0);
}
