void mainImage( out vec4 O, in vec2 I )
{
    float u, i, v, t = iTime;
    vec3 r = iResolution, b = vec3(0, 20, 0), 
    c = n(vec3((I-.5*r.xy)/r.y, 1.));

    for(;i++<1e2;)
    {
        vec3 p = b + c * u;
        p.zy *= R2(-.5);
        vec2 z, y;

        float d = p.y, a = 1., f = .1, i, e, g;
        for(;i++<15.;)
        {
            g = R(i);
            p.xz *= R2(g);
            z = a*sin(f*p.xz + t*f*20.);
            y = a*f*vec2(cos(g), sin(g))*cos(f*p.xz + t*f*20.);
            d += z.x+z.y;
            e += y.x+y.y;
            a *= 0.6;
            f *= 1.25;    
        }
        v = d;
        u += v;
        O=(v < .01)?vec4(vec3(1.)*max(dot(cross(vec3(1, 0, e), vec3(0, 1, e)), n(vec3(0, 5, 6))), 0.),1.):O;
        b += c;
    }
}

#define PI 3.14159265358979323846
mat2 R2(float angle)
{
    float s = sin(angle);
    float c = cos(angle);
    return mat2(c, -s, s, c);
}

float R(float n){return fract(sin(n) * 43758.5453123);}

float n(float a){return normalize(a);}
vec2 n(vec2 a){return normalize(a);}
vec3 n(vec3 a){return normalize(a);}
vec4 n(vec4 a){return normalize(a);}
