void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    vec3 col = vec3(1.0, 2.0, 3.0);

    float d = length(uv);
    float radius = 0.5 + sin(iTime * 5.0)*0.1;

    float rgb;

    float radiusDistance = abs(d-radius);

    rgb = smoothstep(0.02, 0.1, radiusDistance);
    rgb = 0.1/radiusDistance;

    col *= rgb;

    fragColor = vec4(col, 1.0);
}
