#define R iResolution.xy
#define mi 20.
vec2 f(vec2 p)
{
    return vec2(p.y-p.x,1.-p.y*p.y);
}
float iter(vec2 z, vec2 c)
{
    for (float i; i<mi;i++)
    {
        z=f(z)+c;
        if (dot(z,z)>4.) return i;
    }
    return mi;
}
void mainImage(out vec4 O,vec2 F)
{
    F=(F-R*.5)/min(R.x,R.y)*4.;
    vec2 C = (iMouse.xy-R*.5)/min(R.x,R.y)*2.;
    O=vec4(iter(F,C)/mi);
    if (length(F-vec2(0,-.35))<.02) O=vec4(1,0,0,1);
}
