#ifndef saturate
#define saturate(v) clamp(v,0.,1.)
//      clamp(v,0.,1.)
#endif

vec3 drawline(vec3 col_i, float coord, float offset, float zoom_off, float width, float period, vec3 color) {
	if( mod( abs(coord + offset * zoom_off) , period) > width){
        col_i = color;
    };
    return col_i;
}


vec3 hsv2rgb(vec3 c){
	vec4 K=vec4(1.,2./3.,1./3.,3.);
	return c.z*mix(K.xxx,saturate(abs(fract(c.x+K.xyz)*6.-K.w)-K.x),c.y);
}

vec3 scratchlike_color(float hue, float sat, float val){
    return hsv2rgb( vec3( mod(hue, 1.0),sat,val));
}


vec2 rotate_uv(float degr, vec2 uv){
    float rot_x = float(uv.x * cos(degr))+(uv.y * sin(degr));
    float rot_y = float(uv.y * cos(degr)) - (uv.x * sin(degr));

    return vec2( rot_x , rot_y );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ((fragCoord-iResolution.xy/2.0)/iResolution.x)*250.0;

    float rot_x = float(uv.x * cos(iTime/4.0))+(uv.y * sin(iTime/4.0));
    float rot_y = float(uv.y * cos(iTime/4.0)) - (uv.x * sin(iTime/4.0));

    //uv = vec2( rot_x , rot_y );

    //vec2 offset = vec2(sin(iTime/20.)*20., -sin(iTime/20.)*20.) * 25.0;

    vec2 offset = vec2(iTime * 10., 0);

    //float zoomlvl = 2. + (1.5 - pow(sin(iTime/1.), 4.)*1.5);

    float zoomlvl = 1.;

    uv = uv * zoomlvl;

    // Time varying pixel color

    //vec3 col = 0.5 + 0.5*cos(uv.xyx+vec3(0,2,4));
    vec3 col = scratchlike_color(0.1,0.65,0.9);

    vec3 cl_1 = vec3(0.0, 0.0, 0.0);

    float sigma_linecount = 4.0;

    for(float i = 1.0; i <= sigma_linecount; i++)
    {   
        float mscale = 4.;

        float istep = i/sigma_linecount;

        float istep_mscaled = istep * mscale;


        float powfc = 1.75;// + (0.5 + cos(iTime/2.)/2.);

        float curved_i = pow(istep_mscaled, powfc);
        float opposite_c_i = pow(sigma_linecount, powfc) - curved_i;

        float stepping_sine = pow(sin(iTime * curved_i /6.0 + curved_i /2.0) + 1.5, 0.15);

        if (mod(uv.y - iTime*6., 30.) < 15.){
            uv.x += stepping_sine/1.5 + (60. + curved_i*7. - (20. + curved_i * 5.75)) * 1.05;
        }

        vec2 local_uv = rotate_uv(sin(iTime/1. + curved_i)/4., uv);

        local_uv.x = local_uv.x + sin(local_uv.y/20.+iTime*3.)*2.;

        offset.x +=  stepping_sine/0.4;

        cl_1 = scratchlike_color(0. + curved_i/500.0,0.65,0.9-curved_i/20.);
        col = drawline(col, local_uv.x, offset.x, (1.0 + curved_i /3.0), 20. + curved_i * 5.75, 60. + curved_i*7. , cl_1);
        //col = drawline(col, uv.y, offset.y, (1.0 + curved_i /7.0), 14. + curved_i * 3.25, 15. + curved_i*4. , cl_1);
    }




    /*
    cl_1 = scratchlike_color(.09);

    col = drawline(col, uv.x, offset.x, 1.15, 14, 22, cl_1);
    col = drawline(col, uv.y, offset.y, 1.15, 14, 22, cl_1);

    cl_1 = scratchlike_color(.14);

    col = drawline(col, uv.x, offset.x, 1.3, 22, 36, cl_1);
    col = drawline(col, uv.y, offset.y, 1.3, 22, 36, cl_1);

    cl_1 = scratchlike_color(.2);

    col = drawline(col, uv.x, offset.x, 1.45, 35, 52, cl_1);
    col = drawline(col, uv.y, offset.y, 1.45, 35, 52, cl_1);
    */

    // Output to screen
    fragColor = vec4(col,1.0);


}
