vec2 hash2d(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+19.19);
    return fract((p3.xx+p3.yz)*p3.zy) - 0.5;
}
float hash21(vec2 p) {
    p = fract(p*vec2(234.34, 435.345));
    p += dot(p, p+34.23);
    return fract(p.x*p.y);
}


float sdCircle( vec2 p, float r )
{
    return length(p) - r;
}
float sdRing( vec2 p)
{
    return log(length(p)) * length(p) * -1.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec2 uvm = uv;
    uvm.x += iTime * 0.01;
    float scale = 20.0;
    uvm *= scale;

    vec3 col = vec3(0.0);


    /// stars
    vec2 gv = fract(uvm)-0.5;
    vec2 id = floor(uvm);

    col += smoothstep(scale/iResolution.y, -scale/iResolution.y,sdCircle(gv + hash2d(id) * 0.8, 0.05 + hash21(id) * 0.03)) * smoothstep(-scale/iResolution.y, scale/iResolution.y, sdCircle(uv, 0.1)); 
    ///

    //rijg
    col += sdRing(uv);
    //orb
    col += smoothstep(scale/iResolution.y, -scale/iResolution.y, sdCircle(uv, 0.1)) * vec3(1.0, 0.8, 0.5) * sin(uv.x) * atan(uv.x, uv.y) * 2.0;
    //light beams
    col.xy += vec2(max(0.001, sin(atan(uv.x, uv.y) * 10.0 + iTime) * 0.02)) * smoothstep(-scale/iResolution.y, scale/iResolution.y, sdCircle(uv, 0.1));

    //if (gv.x > 0.48 || gv.y > 0.48) col = vec3(1.0, 0.0, 0.0);

    fragColor = vec4(col,1.0);
}
