const vec3 RED = vec3(1,0,0);
const vec3 PURPLE = vec3(1,0,1);
const vec3 YELLOW = vec3(1,1,0);
const vec3 GREEN = vec3(0.1,1,0);
const vec3 TURQUOISE = vec3(0,1,1);
const vec3 BLUE = vec3(0,0,1);

vec3 colors[6] = vec3[6](RED, YELLOW, GREEN, TURQUOISE, PURPLE, BLUE);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    // display texture
    vec3 col = texture(iChannel0, uv).xyz;        
    //Color used
    vec3 color = colors[0];
    float amount = 7.;
    float brightness = (col.x * 1.5 + col.y * 1.3 + col.z*1.1)/3.;

    //Changes color based on brightness
    for(int i = 1; float(i) < amount; i++){
        float num = amount - float(i);
        if(brightness <= 0.00){
            col = vec3(0.);
        }
        else if(brightness <= (1. / num)){
            col = vec3(colors[int(num) - 1]/num);
            break;
        }
    }
    //col = floor(brightness * amount) / amount * colors[int(amount - floor(brightness * amount))];
    // Output to screen
    fragColor = vec4(col,1.0);
}
