// -2 thanks to coyte

void mainImage(out vec4 o, vec2 u) 
{
    vec2 b = iResolution.xy,
         a = (u+u-b)/b.y;
    a /= (1. + .2*dot(a,a)) * cosh(.6*iTime);
    o *= 0.;
    for (int i; i++ < 30; length(a) > 1e5 ? o++ : o) 
        b = cos(a.x+vec2(0, 1.5708))/exp(a.y),
        a = 8.*mat2(a, a.y, -a)*b - a;      
}

