void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;

    float d = length(uv);
    vec3 col = vec3(.75, .50, .25) / d;

    // Output to screen
    fragColor = vec4(col,1.0);
}
