// upside down pyramid

// SDF primitives taken from:
// and iquilezles.org/articles/distfunctions2d

float sdPyramid( in vec3 pos, in vec2 hw_h)
{
    // flip because blender is Z up
    pos.xyz = pos.xzy;

    pos.xy = abs(pos.xy);
    pos.xy = (pos.y>pos.x) ? pos.yx : pos.xy;

    vec3 al = vec3(hw_h.x, hw_h.x, hw_h.y);
    vec3 bl = vec3(0.0, 0.0, 0.0);

    vec3 pa = pos - al, ba = bl - al;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    float cornerdist = length( pa - ba*h );

    // based on isosceles triangle by iq https://www.shadertoy.com/view/MldcD7    
    float p_to_q = clamp( dot(pos.xz,hw_h)/dot(hw_h,hw_h), 0.0, 1.0 );
	vec2 a = pos.xz - hw_h*p_to_q;
    vec2 b = pos.xz - hw_h*vec2( clamp( pos.x/hw_h.x, 0.0, 1.0 ), 1.0 );
    float k = sign( hw_h.y );
    float d = min(dot( a, a ),dot(b, b));
    float s = max( k*(pos.x*hw_h.y-pos.z*hw_h.x),k*(pos.z-hw_h.y)  );
	float facedist = sqrt(d)*sign(s);

    float cutoff_halfwidth = p_to_q*hw_h.x;
    return (pos.y >= cutoff_halfwidth) ? cornerdist : facedist; 
}


float map( in vec3 pos )
{
    float rad = 0.2*(0.5-0.45*cos(iTime*3.0));
    float hei = 1.5*(0.5+0.45*sin(iTime*1.0));
    float halfwidth = 1.0*(0.5+0.45*sin(iTime*2.0));
    return sdPyramid(pos,vec2(halfwidth, hei) ) - rad;
}

// https://iquilezles.org/articles/normalsSDF
vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773;
    const float eps = 0.001;
    return normalize( e.xyy*map( pos + e.xyy*eps ) + 
					  e.yyx*map( pos + e.yyx*eps ) + 
					  e.yxy*map( pos + e.yxy*eps ) + 
					  e.xxx*map( pos + e.xxx*eps ) );
}

#if HW_PERFORMANCE==0
#define AA 1
#else
#define AA 3
#endif

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     // camera movement	
	float an = 0.5*iTime;
	vec3 ro = vec3( 3.0*cos(an), 1.0, 3.0*sin(an) );
    vec3 ta = vec3( 0.0, 0.5, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));



    vec3 tot = vec3(0.0);

    #if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
        #else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
        #endif

	    // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 1.8*ww );

        // raymarch
        const float tmax = 30.0;
        float t = 0.0;
        for( int i=0; i<1024; i++ )
        {
            vec3 pos = ro + t*rd;
            float h = map(pos);
            if( h<0.0001 || t>tmax ) break;
            t += h;
        }


        // shading/lighting	
        vec3 col = vec3(0.0);
        if( t<tmax )
        {
            vec3 pos = ro + t*rd;
            vec3 nor = calcNormal(pos);
            float dif = clamp( dot(nor,vec3(0.7,0.6,0.4)), 0.0, 1.0 );
            float amb = 0.5 + 0.5*dot(nor,vec3(0.0,0.8,0.6));
            col = vec3(0.2,0.3,0.4)*amb + vec3(0.0,0.7,0.1)*dif;
        }

        // gamma        
        col = sqrt( col );
	    tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif

	fragColor = vec4( tot, 1.0 );
}
