mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

vec3 getColor(int index){
    vec3 colors[7];
    colors[0] = vec3( 1.0, 0.0, 0.0 );
    colors[1] = vec3( 1.0, 0.5, 0.0 );
    colors[2] = vec3( 1.0, 1.0, 0.0 );
    colors[3] = vec3( 0.0, 1.0, 0.0 );
    colors[4] = vec3( 0.0, 0.75, 1.0 );
    colors[5] = vec3( 0.0, 0.0, 1.0 );
    colors[6] = vec3( 0.65, 0.0, 1.0 );
    return colors[index];
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{        
    vec2 st = (fragCoord.xy * 2. - iResolution.xy) / iResolution.y;
    vec3 col = vec3(0.15, 0., 0.3);

    float spiralNumber = 7.0;

    for (float i = 0.; i < spiralNumber; i= i+1.){
        vec2 translate = vec2(cos(0.1 + (i+1.)/5.),sin(0.1 + (i+1.)/5.));        
        st += translate * 0.1 ;        
        st = (rotate2d(0.5 * i * 3.14)) * st;

        float polar = sqrt(pow(st.x, 2.) + pow(st.y, 2.));   

        float a = atan(-st.x,st.y);

        float rotation = 0.4;
        float lengthSpriral = .55 - sin(iTime * 10. - i/7.)*0.005;

        float spiral = rotation + a * lengthSpriral;

        float line = smoothstep(spiral-spiral/15.,spiral,polar) 
        - smoothstep(spiral,spiral + spiral/15.,polar);

        vec3 thisCol = vec3(line);      

        thisCol *= sin(iTime * 3. + -i * 1. - spiral * 1.0);
        thisCol *= getColor(int(i));

        col = max(thisCol, col);
    }

    fragColor = vec4(col,1.0);
}
