const float pi = 3.1415926536;

// center coordinates
float x0 = 0.;
float y0 = 0.;

const int n = 5;
float r = 60.; // radius
float a = 360./float(n); // angle

float sqr(float x) {
  return x*x;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  float w2 = iResolution.x / 2.;
  float h2 = iResolution.y / 2.;
  float m = h2 < w2 ? h2 : w2;

  // make complex radius from r and a:
  float rr = r * cos(-a*pi/180.) / m;
  float ri = r * sin(-a*pi/180.) / m;

  float cx = fragCoord.x - w2;
  float cy = fragCoord.y - h2;
  float dx  = cx*rr - cy*ri + x0;
  float dy  = cx*ri + cy*rr + y0;
  float t = iTime/3.;  

  float g = 0.;
  for(int i = 0; i < n; ++i) {
    float ax  = cos(a*float(i)*pi/180.);
    float ay  = sin(a*float(i)*pi/180.);
    g += sin(ay*dy+ax*dx+t*float(i));
  }
  g = g / float(n);
  if(g < 0.)
    g = 0.;
  else
    g = 1.;
  fragColor = vec4(g, g, g, 0.);
}
