float random (vec2 st) {
    return fract(sin(dot(st.xy,vec2(12.9898,78.233)))*43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float pi=3.14;
    float size = 1.0;
    vec2 pos = fragCoord - floor(fragCoord / size) * size;
    vec3 col = vec3(0.0,0.0,0.0);
    vec4 img = texture(iChannel0, uv);
    //float comp = /64.0


    // grayscale = 0.299 ∙ Red + 0.587 ∙ Green + 0.114 ∙ Blue
    float grays = 0.299 * img.x + 0.587 * img.y + 0.114 * img.z;
    //grays=0.5*(grays+(fragCoord.x)/(iResolution.x));
    //grays=(fragCoord.x)/(iResolution.x);
    //grays=0.5*(grays+0.5);
    //grays=0.5;

    float periodT,periodT0,periodT1;
    float numDis = 1.00;
    float width = 10.0;
    periodT= width*(1.0 - floor(grays*numDis)/numDis);
    periodT=10.0;
    periodT0=1.0*periodT;
    periodT1=1.0*periodT;

    float func0,func1,func;
    float theta=10.00*iTime;
    //vec2 po = vec2(0, 0);
    vec2 po = vec2(iResolution.x/2.0,iResolution.y/2.0);
    vec2 dir0 = vec2(cos(pi*theta/180.+pi/2.0), sin(pi*theta/180.+pi/2.0));
    //theta=15.;
    vec2 dir1 = vec2(cos(pi*theta/180.), sin(pi*theta/180.));

    func0 = dot((fragCoord-po), dir0)-0.00*iTime;
    func1 = dot((fragCoord-po), dir1)-.0-0.00*iTime;


    //func= 81.0* (abs(func0) + abs(func1)); //Rhombus
    //func= func0*func0 + func1*func1; //Circle
    //func= 81.0* sqrt(abs(func0*func1)); //Hyperbola
    //func= 81.0*(1.00*func0 - sqrt(func1*func1+1.0)); //Parabola (use periodT=30 or above)
    //func= func0 - func1*func1; //Real Parabola //(use periodT=5000 or above)
    //func= (81.0*func0 - func1*func1); //Parabola (use periodT=30 or above)
    func= func0*func1; //Hyperbola
    //func= 0.50*(30.0*sin(func0/10.0)-func1); //sine function
    //func= 1.0*(sin(func0/1.50)+sin(func1/1.50)); //sine function
    func = func /(sqrt(abs(func+1.5))*periodT);
    //func = func /periodT;
    func += 1.00*iTime;    
    func = abs(func - floor(func+0.5));

    //func = sin(func);


   //col = vec3(0.1,0.2,0.3);

    float r=0.00; //Amount of noise for antialising 
    //col = vec3(ceil(grays - 2.0*func + r*(random (fragCoord)-0.5)));
    //col = vec3(grays);
    float red, green,blue; 
    red = ceil(img.x - 2.0*func + r*(random(fragCoord)-0.5));
    green = ceil(img.y - 2.0*func + r*(random(fragCoord)-0.5));
    blue = ceil(img.z - 2.0*func + r*(random(fragCoord)-0.5));
    col = vec3(red,green,blue);

    // Output to screen
    fragColor = vec4(col,1.0);//vec4(col,1.0); img;

}
