/*
properties:
  resolution: { value: [500, 500], editor: { tooltip: '图片尺寸' }  }
  offset: { value: 1.0, range: [0.0, 10.0] , editor: { slide: true, range: [0.0, 10.0], step: 0.01, tooltip: '偏移距离' } }
*/

vec4 kawaseBlur(sampler2D tex, vec2 uv, vec2 texelSize, float offset) {
	vec4 o = vec4(0);
    // 通俗理解: 把这个点周围四个方向相距的offset对应的点的颜色取平均.
	o += texture(tex, uv + vec2(offset + 0.5, offset + 0.5) * texelSize); 
	o += texture(tex, uv + vec2(-offset - 0.5, offset + 0.5) * texelSize); 
	o += texture(tex, uv + vec2(-offset - 0.5, -offset - 0.5) * texelSize); 
	o += texture(tex, uv + vec2(offset + 0.5, -offset - 0.5) * texelSize); 
	return o * 0.25;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv0 = fragCoord/iResolution.xy; // 0.0<>1.0
    vec2 resolution = iResolution.xy;
    float offset = abs(sin(iTime)*2.0); // 0.0<>2.0

    vec4 col = kawaseBlur(iChannel0, uv0, 1.0 / resolution, offset);

    fragColor = col;
}
