void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    bool linhaPar = int(fragCoord.y)%2==0;  

    float i = iTime;
    vec2 uv = fragCoord.xy / iResolution.xy;


    vec4 c = texture(iChannel0,uv);

    if(linhaPar){
        fragColor = vec4(.2*c.r +  .7*c.g + .1*c.b);    
    }
    else{
        fragColor = vec4(c.r,0.,0.,0.);    
    }


}
