const float wscale = 3.0;
const float gscale = 6.0;
const float tscale = 1.0;

vec2 warp(vec2 xy, float t) {
    // return vec2(cos(t), sin(t)) * cos(xy) * sin(xy.yx); // uncomment me!
    return cos(t) * sin(xy.yx) + sin(t) * cos(xy.yx);
}

float grid(vec2 xy) {
    xy = abs(mod(xy, 1.0) - 0.5);
    float d = min(xy.x, xy.y);
    return smoothstep(1.0, 0.0, 3.0 * d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    xy += 0.5 * warp(wscale * xy, tscale * iTime) / wscale;

    vec3 col = vec3(1.0, 0.0, 0.0) * grid(gscale * xy);

    fragColor = vec4(col,1.0);
}
