#define TIME mod(iTime/2.0,10.0)

float st(float s) { return smoothstep(0.99,1.0,s); }

vec3 japan(vec2 uv) {
    vec3 col = vec3(0.0); // Fundo preto (incluindo bordas)

    // Verifica se está dentro das bordas
    if (abs(uv.x) < 1.5 && abs(uv.y) < 0.9) {
        col = vec3(1.0); // Fundo branco
        col = mix(col, vec3(1.0, 0.0, 0.0), 1.0 - st(length(uv*2.0))); // Círculo vermelho menor
    }
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (2.0*fragCoord.xy-iResolution.xy)/iResolution.y;

    float x = uv.x*2.0+iTime*1.5;

    uv.y *= 1.0+sin(x)*0.05;

    vec3 col = japan(uv*1.25)*(3.0-sin(x))/3.0;

    fragColor = vec4(col,1.0);
}
