vec2 cmul(vec2 a, vec2 b)
{
    return vec2(a.x * b.x - a.y * b.y, a.x * b.y + a.y * b.x);
}
void mainImage(out vec4 O, in vec2 I)
{
    vec2 uv = vec2(3.56995, 0.) + (vec2(-2.5, 0.) + 2. * (2. * I - iResolution.xy) / iResolution.y) / exp2((iTime * iTime) / (iTime + 4.));
    vec2 Z = vec2(0.5, 0.);
    float dist = 100.;
    for(uint i; i < 1024u; i++)
    {
        Z = cmul(uv, cmul(Z, (vec2(1., 0.) - Z)));
        dist = min(dist, length(Z));
    }
    vec3 col = vec3(1. / (dist * (iTime + 8.)));
    O = vec4(col,1.0);
}
