float dot2(in vec2 v ) { return dot(v,v); }

float sdCircle( vec2 p, float r )
{
    return step(length(p) - r, 0.01);
}

float sdTrapezoid( in vec2 p, in float r1, float r2, float he )
{
    vec2 k1 = vec2(r2,he);
    vec2 k2 = vec2(r2-r1,2.0*he);
    p.x = abs(p.x);
    vec2 ca = vec2(p.x-min(p.x,(p.y<0.0)?r1:r2), abs(p.y)-he);
    vec2 cb = p - k1 + k2*clamp( dot(k1-p,k2)/dot2(k2), 0.0, 1.0 );
    float s = (cb.x<0.0 && ca.y<0.0) ? -1.0 : 1.0;
    return s*sqrt( min(dot2(ca),dot2(cb)) );
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)

    vec2 uv = (2.0 *fragCoord - iResolution.xy) / iResolution.y;

    vec2 newUV = uv + vec2(cos(iTime), sin(iTime)) * 0.3;
    vec2 texUV = fragCoord / iResolution.xy;


    float circle = sdCircle(newUV + vec2(0.0, -0.3), 0.35);

    float trapezoid = sdTrapezoid(newUV, 0.4, 0.02, 0.6);


    vec3 mask = max(vec3(1.0) - vec3(circle) - vec3(step(trapezoid, 0.01)), 0.0);

    vec4 tex = texture(iChannel0, texUV);

    vec3 col = mix(tex.rgb, vec3(0.0), mask);


    // Output to screen

    fragColor = vec4(col, 1.0);
}
