#define PI 3.141529


mat2 rot2(float a)
{


    float s = sin(a);
    float c = cos(a);

    return mat2(c, -s, s, c);

}




vec3 palette(float t) 
{

	vec3 a = vec3(0.600, 0.5, 0.5);
	vec3 b = vec3(0.5, 0.5, 0.5);
	vec3 c = vec3(1.0, 1.0, 1.0);
	vec3 d = vec3(0.00, 0.633, 1.966);

	return a + b * cos(6.281318 * (c * t * d));

}



float wstar(vec2 uv, float i)
{

    uv *= rot2(i);
    uv *= 7.0;

    float f = (0.1 / length(uv.x * uv.y * 2.0)) * (1.0 - length(uv));
    vec2 uvd = uv * rot2(45.0 * (PI / 180.0));
    f += (0.1 / length(uvd.x * uvd.y * 2.0)) * (1.0 - length(uvd) * 1.86);
    f += 0.3 / (length(uv) - 0.01);
    return f;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;
    uv.x *= (iResolution.x / iResolution.y);

    uv.x += sin((uv.y + iTime * 0.5) * 4.0) * 0.2;
    uv.y += sin((uv.x + iTime * 0.5) * 3.0) * 0.1;
    uv.x += sin((uv.y + iTime * 0.5) * 17.0) * 0.02;
    uv.y += sin((uv.x + iTime * 0.5) * 13.0) * 0.01;

    uv *= 0.11;
    vec3 color = vec3(0.0);

    for (float i = 0.0; i < 3.0; i++)
    {



        color.r += wstar(uv + vec2(cos(i + iTime), sin(i + iTime)) * i * 0.075, i * 2.0);
        color.g += wstar(uv + vec2(cos(i + iTime), sin(i + iTime)) * i * 0.075, i * 2.0);
        color.b += wstar(uv + vec2(cos(i + iTime), sin(i + iTime)) * i * 0.075, i * 2.0);

    }

    color *= palette(iTime * 0.1);

    fragColor = vec4(0.1 - color, 1.0);
}
