#define I iResolution
#define g 50.
#define A min( I.x, I.y )
#define rot( a ) mat2( cos(a), sin(a), -sin(a), cos(a) )

float noise(vec3 v){

    for( float i = 1.; i < 4.; i++ ){
        v.x += sin( v.x ) * sin( v.y ) * i / 4.;
        v.y += clamp( v.x / v.y, -1., 1. );
        v.z += cos( v.y * i * v.x ) * cos( v.y / i * 20. ) * i / 4.;
    }

    return v.z;
}


float gf( in vec2 u, float a, float s ){

    float p;

    u *= rot( a );

    p += noise( vec3( u.x, a, s ) ) * .25;

    p -= fract( p - iTime * .1 ) * .2;

    p = mix(
        p,
        0.,
        smoothstep( 0., s * (.6 - sin( iTime * 2. ) * .3), length( vec2( u.x, u.y / 2. + .25 ) - vec2( .5 + cos( a ) * s * .01, .5 + sin( a ) * s * .01 ) ) )
    );


    p *= s * 5e-7;

    return p;

}

void mainImage( out vec4 U, in vec2 V )
{
    vec2 u = ( V * 2. - I.xy ) / A;


    float p, s = .8;

    for( float i = 0.; i < g; i++ ){

        p = mix(
            p,
            i / g,
            abs( p - gf( u, i - fract( iTime  ), .5 + ( i / g ) * s * .9 + abs( cos( iTime ) * .15 ) ) )
        );

    }

    U = vec4(p);
}
