void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Transform the pixel coordinates to the center of the screen
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    float radius = length(uv);
    radius = radius + iTime * 0.25; //move the rings move inward

    //ring pattern using a sine wave
    float ringPattern = step(sin(radius * 17.0), 0.0);

    //gradient background
    vec3 yellow = vec3(1.0, 1.0, 0.0);
    vec3 teal = vec3(0.0, 1.0, 1.0); 
    vec3 gradient = mix(teal, yellow, uv.x + 1.0);

    //ring pattern over the gradient background
    vec3 color = mix(gradient, vec3(0.0), ringPattern);

    // Output to screen
    fragColor = vec4(color, 1.0);
}
