vec2 centre;
float r;
vec2 centrel;
float rl;
void setCircle(){
    r = 0.18;
    centre = vec2(sin(iTime)*0.25,cos(iTime)*0.25);
}
void setCircleL(){
    rl = 0.26;
    centrel = vec2(cos(iTime)*0.25, sin(iTime)*0.25);
}
void setBG(out vec3 BGColor){
    BGColor = vec3(0.2, 0.7 , 0.7);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 BGColor;
    setBG(BGColor);
    setCircle();
    setCircleL();
    fragColor = vec4(BGColor, 1.0);
    vec2 tmp = fragCoord/iResolution.xy;
    tmp -= 0.5; //made the coordinate center in the middle
    tmp.x *= iResolution.x/iResolution.y; //alignment
    if ((tmp.x - centrel.x)*(tmp.x - centrel.x) + (tmp.y - centrel.y)*(tmp.y - centrel.y) <= rl*rl){
        fragColor = vec4(0.3, 0.3, 0.3, 0.0);
    }
    if (0.56*tmp.x <= -tmp.y && tmp.x >= -0.25 && tmp.y >= -0.18) fragColor = vec4(0.0, 0.0, 0.0, 0.0);
    if ((tmp.x - centre.x)*(tmp.x - centre.x) + (tmp.y - centre.y)*(tmp.y - centre.y) <= r*r){
        fragColor = vec4(0.9, 0.9, 0.9 , 0.0);
    }
}
