// Modeling - 2024.02.06
// Eric Galin

// Rotation matrix around z axis
// a : Angle
mat3 Rz(float a)
{
  float sa=sin(a);float ca=cos(a);
  return mat3(ca,sa,0.,-sa,ca,0.,0.,0.,1.);
}

struct Ray {
vec3 ro; // Ray origin
vec3 rd; // Direction
};

// Compute a point on the ray
// ray : Ray
// t   : depth
vec3 Point(Ray ray,float t)
{
  return ray.ro+t*ray.rd;
}

// Compute the ray
//     m : Mouse position
//     p : Pixel
Ray CreateRay(vec2 m,vec2 p)
{
  float a=3.*3.14*m.x; 
  float le=3.5;

  // Origin
  vec3 ro=vec3(35.,0.,10.)*Rz(a);

  // Target point
  vec3 ta=vec3(0.,0.,3.);

  // Orthonormal frame
  vec3 w=normalize(ta-ro);
  vec3 u=normalize(cross(w,vec3(0.,0.,1.)));
  vec3 v=normalize(cross(u,w));
  vec3 rd=normalize(p.x*u+p.y*v+le*w);
  return Ray(ro,rd);
}

// Primitives

// Sphere
// p : point
// c : center of skeleton
// r : radius
float Sphere(vec3 p,vec3 c,float r)
{
  return length(p-c)-r;
}

// Plan
// p : point
// n : norme du plan
// c : centre
float Plan(vec3 p,vec3 n, vec3 c)
{
  n= normalize (n);
  return dot(p-c,n);
}


// Tore
// p : point
// c : centre
// r1 : rayon section centrale 
// r2 : rayon cercle
float Tore(vec3 p, vec3 c, float r1, float r2)
{
    p=p-c;
  vec2 q = vec2(length(p.xz) - r1, p.y);
  return length(q) - r2;
}

//Cylindre
//p : point
//c : centre
//h : hauteur
//r : rayon

float Cylindre(vec3 p, vec3 c, float h, float r)
{
    vec2 d =abs(vec2(length(p.xz - c.xz),p.y - c.y)) - vec2 (r,h);
    return min(max(d.x, d.y), 0.0) + length(max(d, 0.0));
 }

  // Capsule
float capsule(vec3 point, vec3 start, vec3 end, float radius)
{
    vec3 direction_unit = normalize(end - start); // Normalisation du vecteur direction de la capsule
    float distance_to_start = dot(point - start, direction_unit); // Calcul de la distance entre le point et le début de la capsule

    float distance_to_surface;

    if (distance_to_start < 0.0)
    {
        // Point à l'extérieur de la capsule près de l'extrémité de départ
        distance_to_surface = length(point - start) - radius; 
    }
    else if (distance_to_start > length(end - start))
    {
        // Point à l'extérieur de la capsule près de l'extrémité de fin
        distance_to_surface = length(point - end) - radius; 
    }
    else
    {
        // Point à l'intérieur de la capsule
        float distance_to_axis = sqrt(length(point - start) * length(point - start) - distance_to_start * distance_to_start);
        distance_to_surface = distance_to_axis - radius; 
    }

    return distance_to_surface; 
}

// Cone
// p : point
// c : centre de la base
// h : hauteur
// r : rayon de la base
float Cone(vec3 p, vec3 c, float h, float r)
{
    vec3 q = p - c;
    vec2 d = vec2(length(q.xz), q.y);
    vec2 p0 = vec2(0.0, 0.0); // Base du cône
    vec2 p1 = vec2(h, r); // Sommet du cône

    float b = (p1.y - p0.y) / p1.x;
    float a = (p1.y - b * p1.x);

    float dist = (d.y - (b * d.x + a)) / sqrt(b * b + 1.0);
    float distBase = length(max(vec2(d.x - p0.x, d.y - p0.y), 0.0));
    float distTop = length(max(vec2(d.x - p1.x, d.y - p1.y), 0.0));

    float res = max(-dist, distTop - h);
    res = min(res, distBase - r);
    return res;
}


// Operators

// Union
// a,b : field function of left and right sub-trees
float Union(float a,float b)
{
  return min(a,b);
}
// Union, extension to four sub-trees
// a,b,c : field function of left and right sub-trees
float Union(float a,float b,float c,float d)
{
  return min(min(a,b),min(c,d));
}

// intersection
float Intersection(float a,float b)
{
  return max(a,b);
  }

// Difference
// a,b : field function of left and right sub-trees
float Difference(float a,float b)
{
  return max(a,-b);
}


 float boite(vec3 p)
{
    float v1 = Intersection(Plan(p, vec3(2., 0., 0.), vec3(2., 0., 0.)), Plan(p, vec3(0., 2., 0.), vec3(0., 2., 0.)));
    v1 = Intersection(v1, Plan(p, vec3(0., 0., 2.), vec3(0., 0., 2.)));

    v1 = Intersection(v1, Plan(p, vec3(-2., 0., 0.), vec3(-2., 0., 0.)));
    v1 = Intersection(v1, Plan(p, vec3(0., -2., 0.), vec3(0., -2., 0.)));
    v1 = Intersection(v1, Plan(p, vec3(0., 0., -2.), vec3(0., 0., -2.)));

    return v1;
}


// Potential field of the object
// p : point
float object(vec3 p)
{
float v=boite(p);
   //float v;

  v = Union(v, Tore(p, vec3(-5., 10., 5.), 2., 1.));
      // v = Tore(p, vec3(-5., 10., 5.), 4., 1.);

   v = Union(v,Sphere(p,vec3(0.,15.,-9.),3.));
      //v = Sphere(p,vec3(0.,15.,0.),7.);

   v = Union(v, Cylindre(p, vec3(0.0, 15.0, 0.0), 2.0, 2.0));
     // v = Cylindre(p, vec3(0.0, 0.0, 0.0), 7.0, 2.5);

   float capsule_dist = capsule(p, vec3(7.0), vec3(10.0, 5.0, 0.0), 1.0);
   v = Union(v, capsule_dist);
      //float capsule_dist = capsule(p, vec3(5.0), vec3(10.0, 0.0, 0.0), 1.5);
     //v =  capsule_dist;
  v=Union(v,
    Sphere(p,vec3(0.,0.,-25.),20.)
  );

  return v;
}

// Analysis of the scalar field

const int Steps=200;// Number of steps
const float Epsilon=.01;// Marching epsilon

// Object normal
// p : point
vec3 ObjectNormal(vec3 p)
{
  const float eps=.001;
  vec3 n;
  float v=object(p);
  n.x=object(vec3(p.x+eps,p.y,p.z))-v;
  n.y=object(vec3(p.x,p.y+eps,p.z))-v;
  n.z=object(vec3(p.x,p.y,p.z+eps))-v;
  return normalize(n);
}

// Trace ray using ray marching
// ray : Ray 
// e : Maximum distance
// h : hit
// s : Number of steps
float SphereTrace(Ray ray,float e,out bool h,out int s)
{
  h=false;

  // Start at the origin
  float t=0.;

  for(int i=0;i<Steps;i++)
  {
    s=i;
    vec3 p=Point(ray,t);
    float v=object(p);
    // Hit object
    if(v<0.)
    {
      h=true;
      break;
    }
    // Move along ray
    t+=max(Epsilon,v);
    // Escape marched too far away
    if(t>e)
    {
      break;
    }
  }
  return t;
}

// Lighting

// Background color
// d : Ray direction
vec3 Background(vec3 d)
{
  return mix(vec3(.45,.55,.99),vec3(.65,.69,.99),d.z*.5+.5);
}

// Shading and lighting
// p : Point
// n : Normal at point
// e : Eye direction
vec3 Shade(vec3 p,vec3 n,vec3 e)
{
  // Point light
  const vec3 lp=vec3(5.,10.,30.);

  // Light direction to point light
  vec3 l=normalize(lp-p);

  // Ambient color
  vec3 ambient=.25+.25*Background(n);

  // Shadow computation
  float shadow=1.0;
  // Phong diffuse
  vec3 diffuse=.35*clamp(dot(n,l),0.,1.)*vec3(1.,1.,1.);

  // Specular
  vec3 r=reflect(e,n);
  vec3 specular=.15*pow(clamp(dot(r,l),0.,1.),35.)*vec3(1.,1.,1.);
  vec3 c=ambient+shadow*(diffuse+specular);
  return c;
}

vec3 Fibonacci(int i, int n) {
    float phi = float(i) * (3.14159265359 * (3.0 - sqrt(5.0))); // Golden ratio
    float y = 1.0 - float(i) / float(n - 1) * 2.0; // Range from -1 to 1
    float radius = sqrt(1.0 - y * y); // Projected radius onto the xy-plane
    float x = cos(phi) * radius;
    float z = sin(phi) * radius;
    return vec3(x, y, z);
}


float SoftShadow(vec3 p, vec3 n, vec3 lp, int numSamples) {
    float shadow = 0.0;

    for (int i = 0; i < numSamples; ++i) {
        vec3 sampleDir = Fibonacci(i, numSamples);
        Ray shadowRay;
        shadowRay.ro = p + n * Epsilon; // Offset the origin slightly to avoid self-shadowing
        shadowRay.rd = normalize(lp - p + sampleDir * 0.1); // Perturb the ray direction

        bool hit;
        int s;
        float t = SphereTrace(shadowRay, length(lp - p), hit, s);

        if (hit && t < length(lp - p)) {
            shadow += 1.0; // Increment if point is in shadow
        }
    }

    return shadow / float(numSamples); // Normalize the sum of shadow values
}
// Image
void mainImage(out vec4 color, in vec2 pxy)  
{
    // Pixel
    vec2 pixel = (-iResolution.xy + 2.0 * pxy) / iResolution.y;

    // Mouse
    vec2 m = iMouse.xy / iResolution.xy;

    // Camera
    Ray ray = CreateRay(m, pixel);

    // Hit and number of steps
    bool hit;
    int s;

    // Trace ray
    float t = SphereTrace(ray, 75.0, hit, s);

    // Shade background
    vec3 rgb = Background(ray.rd);

    if (hit)
    {
        // Position
        vec3 p = Point(ray, t);

        // Compute normal
        vec3 n = ObjectNormal(p);

        // Calculate shadow using SoftShadow function
        float shadow = SoftShadow(p, n, vec3(5.0, 10.0, 30.0), 100); // Assuming position of the point light and 100 samples

        // Shade object with light and shadow
        rgb = Shade(p, n, ray.rd) * (1.0 - shadow);

    }

    color = vec4(rgb, 1.0);
}
