void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    float d = length(uv) - 0.8;

    vec3 color = d < 0.0 ? vec3(1.0, 0.0, 0.0) : vec3(1.0, 1.0, 0.0);

    vec3 col = vec3(0.);

    float b = cos(10.0*abs(d)+iTime*5.0*sign(d));

    col += clamp(vec3(0.001/abs(b)), 0.0, 1.0) * 360.;

    col = mix(col, color, smoothstep(1.5 , 0.0, abs(b)));

    col += clamp(vec3(0.001/abs(d)), 0.0, 1.0) * 32.;

    col = mix(col, vec3(1.0), smoothstep(20.0/iResolution.y , 0.0, abs(d)));

    // Output to screen
    fragColor = vec4(col,1.0);
}
