float band(float t, float start, float end, float blur)
{
    float step1 = smoothstep(start-blur, start+blur, t);
    float step2 = smoothstep(end+blur, end-blur, t);
    return step1*step2;
}

float rect(vec2 uv, float left, float right, float bottom, float top, float blur)
{
    float band1 = band(uv.x, left, right, blur);
    float band2 = band(uv.y, bottom, top, blur);
    return band1*band2;
}



float getLightSpread(vec2 uv)
{
    return 0.01 + pow((uv.y+0.5) * 0.4, 1.1);
}

float getRootWhitness(vec2 uv)
{
    return pow(2.0*abs(uv.y-0.5),6.0)-0.5;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{

    float aspectRatio = iResolution.x / iResolution.y;
    vec2 uv = ((gl_FragCoord.xy / iResolution.xy) - 0.5) * vec2(aspectRatio,1);

    vec3 col = vec3(0.0);


    uv.x += (uv.y+0.5) * 0.15 * sin(6.0*(uv.y+0.45*iTime));
    uv.y += 0.168;
    uv *= 1.5;

    float mask = 0.0;
    mask += rect(uv, -0.04, 0.04, -0.5, 0.4, getLightSpread(uv));
    mask += rect(uv, -0.04, 0.04, 0.0, 0.5, 0.17);




    col = vec3(1.0, 2.0*abs(uv.y-0.5), getRootWhitness(uv)) * mask;
    fragColor = vec4(col, 1.0);
}
