mat2 rotation2D(float angle) {
    float c = cos(angle);
    float s = sin(angle);
    return mat2(c, -s, s, c);
}

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float dist_to_circle(vec2 p, float r){
    return length(p) - r;
}

float dist_to_circle_at(vec2 p, vec2 c, float r){
    return length(p-c) - r;
}

float dist_to_square(vec2 p, float b){
    vec2 d = abs(p) - b;
    return length(max(d, 0.0)) + min(max(d.x, d.y), 0.0);
}

float dist_to_rotated_square(vec2 p, float theta, float b){
    mat2 rotation = rotation2D(theta);
    p = inverse(rotation)*p;
    return dist_to_square(p, b);
}

float dist_to_circle_and_square(vec2 p){
    float d_square = dist_to_square(p, 0.5);

    // We translate the circle so it doesn't overlap with the square
    float d_circle = dist_to_circle_at(p, vec2(1.2 * sin(iTime/2.),0.0), 0.5);
    return max(d_square, d_circle);
}

float dist(vec2 p){
    //float dist_to_box = sdBox(p+vec2(1.0,0.0), vec2(0.5, 0.5));
    //float d_circle = dist_to_circle(p - vec2(sin(iTime),0.0), 0.4);
    //return min(dist_to_box, d_circle);
    //float d_circle_at = dist_to_circle_at(p, vec2(1.0,0.0), 0.5);
    //float d_rotated_square = dist_to_rotated_square(p, 1.0, 0.5);
    float d_both = dist_to_circle_and_square(p);
    return d_both;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy*2. -1.;

    float maxDim = iResolution.x/iResolution.y;
    if (maxDim < 1.){
        maxDim /= 1./maxDim;
    }
    // Make the grid isotropic
    uv.x *= iResolution.x/iResolution.y;


    // get the distance
    float d = dist(uv);

    // Draw a black line at the edges
    if (abs(d) < 0.01 || (mod(d, 0.1) < 0.005)){
        fragColor = vec4(0.0);
    }
    else
    {
        if (d < 0.){
            d = -mod(-d, 0.1)*10.;

            fragColor = vec4(1.+0.5*d/maxDim,0.0,0.0,0.0);
        }else{
            d = mod(d, 0.1)*10.;

            fragColor = vec4(0.0,1.-0.5*d/maxDim,0.0,0.0);

        }
    }
    // Output to screen

}
