// the palette is good for dynamicity and creating patterns
// check http://dev.thi.ng/gradients/

vec3 palette( float t){
    vec3 a = vec3(0.152, 0.560, 0.926);
    vec3 b = vec3(0.599, 0.265, 0.247);
    vec3 c = vec3(1.557, 0.424, 1.204);
    vec3 d = vec3(5.745, 2.017, 2.988);

    return a + b*cos( 3.*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = (fragCoord * 2. - iResolution.xy) / iResolution.y;

    float d = length(uv * uv.y);

    // I time gives more dynamic to the colors
    vec3 col = palette(d + iTime);


    d = sin(d * 16. - iTime)/8.;
    d = abs(d);

    d = 0.08/d;

    col *= d;

    fragColor = vec4(col, 1.0);
}
