#define R3D(a) mix(q*dot(p,q), p, cos(a)) + sin(a)*cross(p,q)
#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e; vec3 p,q=vec3(0,-1,1),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e)*4.;
    p.z-=6.;
    p.xz*=R2D(iTime*.3);

    for(j=0.;j++<2.;)
        p=abs(abs(p=R3D(i*i/j)-.06))-.04,
        d=min(max(p.z,p.x),p.y)/24.,
        d-=dot(cos(p),sin(p))/1e4;

    e+=d;

}o+=vec4(sqrt(p*p),1)*3./i;
}
