void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy * vec2(2,2);

    float speed = 0.15;    
    vec3 tex01 = texture(iChannel0,uv + iTime*vec2(-speed,0.)).xyz; 
    vec3 tex02 = texture(iChannel1,uv + iTime*vec2(0.,speed)).xyz;     
    vec3 tex03 = texture(iChannel2,uv + iTime*vec2(0.,-speed)).xyz; 
    vec3 tex04 = texture(iChannel3,uv + iTime*vec2(speed,0.)).xyz; 

    vec3 col = vec3(0., 0., 0.);

     if (uv.y>1.0)
    {
        if (uv.x<1.0)  col = tex01;         
        else if (uv.x<2.0)  col = tex02;          
    }

    else
    {
        if (uv.x<1.0)  col = tex03;       
        else if (uv.x<2.0)  col = tex04;      
    }

    vec2 line = pow(smoothstep(0.495,0.4,abs(fract(uv)-0.5)), vec2(0.1)); 

    fragColor = vec4(col * line.x * line.y, .0);
}
